DROP TABLE IF EXISTS public.feedback; CREATE TABLE public.feedback (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE, retro_id UUID NOT NULL REFERENCES public.retrospectives(id) ON DELETE CASCADE, type TEXT NOT NULL, message TEXT NOT NULL, anonymous BOOLEAN NOT NULL DEFAULT false, created_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL); ALTER TABLE public.feedback ENABLE ROW LEVEL SECURITY; CREATE POLICY "Allow all for auth users" ON public.feedback FOR ALL USING (auth.role() = 'authenticated'); GRANT ALL ON public.feedback TO authenticated; 